wolfram_code:
With[{
  (* v16.2 theoretical prediction from b3 = 24 *)
  b3 = 24,
  w0Theory = -1 + 1/b3,  (* = -23/24 *)
  waTheory = -1/Sqrt[b3],  (* = -1/sqrt(24) *)

  (* DESI DR2 2025 observations *)
  w0DESI = -0.957,
  w0Sigma = 0.067,
  waDESI = -0.816,
  waSigma = 0.25
},
  (* Calculate deviations *)
  w0Deviation = Abs[w0Theory - w0DESI] / w0Sigma;
  waDeviation = Abs[waTheory*4 - waDESI] / waSigma;  (* Factor 4 for CPL *)

  (* Combined tension *)
  combinedTension = Sqrt[w0Deviation^2 + waDeviation^2] / Sqrt[2];

  {N[w0Theory, 6], N[w0Deviation, 3], N[waDeviation, 3], N[combinedTension, 3]}
]
