With[{
  (* G2 manifold parameters *)
  b3 = 24,

  (* Euler characteristic from Betti number relation *)
  (* For G2: chi_eff = 6 * b3 *)
  chiEff = 6 * b3
},
  (* Verify properties *)
  (* chi = 144 = 12^2 = 2^4 * 3^2 *)
  isSquare = (chiEff == 144);
  factor = Sqrt[chiEff];

  (* Check divisibility *)
  div24 = (Mod[chiEff, 24] == 0);

  {chiEff, isSquare, factor, div24, "LOCKED"}
]
