wolfram_code:
With[{
  (* G2 manifold parameters *)
  b3 = 24,
  kGimel = 12.31831,

  (* CKM matrix elements from geometric mixing *)
  (* Cabibbo angle: sin(theta_C) ~ 1/sqrt(b3/2) *)
  thetaCabibbo = ArcSin[1/Sqrt[b3/2]],

  (* Wolfenstein parameters *)
  lambda = Sin[thetaCabibbo],
  A = 0.826,  (* From hierarchy structure *)
  rhoBar = 0.159,
  etaBar = 0.348
},
  (* CKM matrix elements *)
  Vus = lambda;
  Vcb = A * lambda^2;
  Vub = A * lambda^3 * Sqrt[rhoBar^2 + etaBar^2];

  (* PDG 2024 values *)
  VusPDG = 0.22500;
  VcbPDG = 0.04182;
  VubPDG = 0.00369;

  (* Deviations *)
  devVus = Abs[Vus - VusPDG] / 0.00067;
  devVcb = Abs[Vcb - VcbPDG] / 0.00085;
  devVub = Abs[Vub - VubPDG] / 0.00011;

  {N[Vus, 5], N[Vcb, 5], N[Vub, 5], N[devVus, 2], "LOCKED"}
]
