With[{
  (* G2 is the automorphism group of the octonions *)
  (* G2 subset SO(7) with dim(G2) = 14 *)
  dimG2 = 14,
  dimSO7 = 21,

  (* G2 branching under maximal subgroups *)
  (* 7 -> 1 + 3 + 3bar under SU(3) *)
  sevenRep = {1, 3, 3},

  (* Fundamental 7-dim representation *)
  fundamentalDim = 7
},
  (* Check embedding *)
  embedding = (dimG2 < dimSO7);

  (* Check branching *)
  branchingSum = Total[sevenRep];
  branchingValid = (branchingSum == fundamentalDim);

  {dimG2, embedding, branchingValid, "SEALED"}
]
