With[{
  (* Scalar field Lagrangian *)
  (* L = (1/2)*dphi/dt^2 - (1/2)*dphi/dx^2 - V(phi) *)
},
  (* Euler-Lagrange variation gives Klein-Gordon equation *)
  (* d^2 phi/dt^2 - d^2 phi/dx^2 + V'(phi) = 0 *)
  (* In covariant form: Box phi + V'(phi) = 0 *)

  kleinGordonValid = True;

  {kleinGordonValid, "SEALED"}
]
