(* Principia Metaphysica - Higgs Sector Derivations *)

(* Step 1: Electroweak VEV *)
MW = 80.377; (* GeV *)
sin2ThetaW = 0.23122;
alphaEM = 1/137.036;
e = Sqrt[4*Pi*alphaEM];
g2 = e / Sqrt[sin2ThetaW];
vEW = 2*MW/g2;
Print["v_EW = ", N[vEW, 5], " GeV"];

(* Step 2: Higgs Quartic *)
mH = 125.10; (* GeV *)
lambdaMZ = mH^2 / (2*vEW^2);
Print["λ(M_Z) = ", N[lambdaMZ, 5]];

(* Step 3: Higgs Potential *)
muSquared = lambdaMZ * vEW^2;
mu = Sqrt[muSquared];
mHCheck = Sqrt[2*muSquared];
Print["μ = ", N[mu, 5], " GeV"];
Print["m_H (check) = ", N[mHCheck, 5], " GeV"];

(* Step 4: Moduli Constraint *)
vYukawa = 174.0; (* GeV *)
lambda0 = 0.129;
kappa = 0.00189;
yTop = 0.995;
lambdaEff = mH^2 / (8*Pi^2*vYukawa^2);
ReT = (lambda0 - lambdaEff) / (kappa * yTop^2);
Print["Re(T) = ", N[ReT, 5]];

(* Step 5: Doublet-Triplet Splitting *)
dOverR = 0.12;
b2 = 4;
etaDT = Exp[-Pi * dOverR * Sqrt[b2]];
Print["η_DT = ", N[etaDT, 5]];

(* Step 6: Yukawa Couplings *)
mTop = yTop * vEW / Sqrt[2];
Print["m_top = ", N[mTop, 5], " GeV"];

(* Step 7: Vacuum Stability *)
MZ = 91.1876; (* GeV *)
MPl = 2.435*10^18; (* GeV *)
betaTop = -6 * yTop^4 / (16*Pi^2);
betaSelf = 12 * lambdaMZ^2 / (16*Pi^2);
betaMix = 12 * lambdaMZ * yTop^2 / (16*Pi^2);
betaPneuma = kappa * ReT * yTop^2 / (16*Pi^2);
betaTotal = betaSelf + betaMix + betaTop + betaPneuma;
t = Log[MPl / MZ];
lambdaMPl = lambdaMZ + betaTotal * t;
Print["λ(M_Pl) = ", N[lambdaMPl, 5]];
Print["Vacuum stable: ", lambdaMPl > 0];
