With[{
  (* G₂ dimensional structure *)
  dG2 = 7,
  dCompact = 3,
  kGimel = 12.31831  (* Holonomy correction *)
},
  (* Effective dimension after compactification *)
  (* d_eff = 7 - 3*(1 - 1/k_gimel) *)

  holonomyFactor = 1 - 1/kGimel;
  dEff = dG2 - dCompact * holonomyFactor;

  (* Dark energy EoS from dimensional reduction *)
  (* For d-dimensional FRW: w = -(d_eff - 1)/(d_eff + 1) *)

  w0Geometric = -(dEff - 1) / (dEff + 1);

  (* Alternative: from pneuma ratio *)
  (* w₀ = -11/13 (from 7D→4D reduction with G₂ holonomy) *)
  w0Exact = -11/13;

  (* DESI 2025 result *)
  desi2025 = -0.727;
  desiUncertainty = 0.067;

  deviation = (w0Geometric - desi2025) / desiUncertainty;

  {N[w0Geometric, 5], N[w0Exact, 5], N[deviation, 3]}
]
