  (* TCS G2 manifold: K3 x S³/Γ structure *)
  b2K3 = 22,
  b1S3Gamma = 0,
  contribution = 2
},
  (* Third Betti number counts independent 3-cycles *)
  (* For twisted connected sum (TCS) G2: *)
  (* b3 = b2(K3) + contribution from S³ bundle *)

  b3 = b2K3 + contribution;

  (* Verify key properties *)
