With[{
  (* Early universe Hubble from Planck *)
  H0early = 67.4,  (* km/s/Mpc *)

  (* G2 modulus parameter *)
  kGimel = 12.318
},
  (* Dynamical dark energy boost *)
  (* H0_late ~ H0_early * (1 + k_gimel/200) *)

  boostFactor = 1 + kGimel/200;
  H0late = H0early * boostFactor;

  (* SH0ES measurement *)
  sh0es = 73.04;
  sh0esErr = 1.04;

  deviation = (H0late - sh0es) / sh0esErr;

  {N[H0late, 4], N[deviation, 3]}
]
