},
  (* Experimental value *)
  alphaExpInv = 137.035999;

  (* Calculate deviation *)
  deviation = Abs[alphaInverse - alphaExpInv] / 0.0001;  (* uncertainty *)

  {N[alphaInverse, 9], N[1/alphaInverse, 9], N[deviation, 2], "LOCKED"}
]
