With[{
  (* String landscape parameters *)
  (* Number of flux vacua ~ 10^500 (Susskind estimate) *)
  landscapeSize = 10^500,

  (* PM reduces this to O(1) via G2 selection *)
  b3 = 24,
  chiEff = 144,

  (* Effective vacua after G2 constraint *)
  effectiveVacua = 1  (* Unique G2 vacuum *)
},
  (* Entropy of landscape *)
  entropyFull = Log[10, landscapeSize];
  entropyEffective = Log[10, effectiveVacua];

  (* Reduction factor *)
  reductionFactor = entropyFull - entropyEffective;

  {entropyFull, entropyEffective, reductionFactor, "SEALED"}
]
