With[{
  chiEff = 144,
  b3 = 24,
  mPlanck = 2.435*^18,
  kGimel = 12.31831
},
  (* M_GUT from flux quantization and modular invariance *)
  (* M_GUT = M_Planck * exp(-2*pi*sqrt(chiEff/b3)) *)
  mGUT = mPlanck * Exp[-2*Pi*Sqrt[chiEff/b3]];

  (* Alternative: from torsion scaling *)
  (* M_GUT ~ M_Planck / kGimel^2 *)
  mGUTTorsion = mPlanck / kGimel^2;

  (* Return both values *)
  {N[mGUT, 5], N[mGUTTorsion, 5]}
]
