With[{
  (* G2 complex structure gives phase *)
  z = 7.102 + 1.054 * I
},
  (* Phase from complex structure *)
  deltaCPIO = Arg[z] * (180/Pi) + 180;

  (* NuFIT 6.0 Inverted Ordering *)
  nufit60IO = 278.0;
  nufit60IOErr = 26.0;

  deviation = (deltaCPIO - nufit60IO) / nufit60IOErr;

  {N[deltaCPIO, 4], N[deviation, 3]}
]
