wolfram_code:
With[{
  (* G₂ topological invariants *)
  b2 = 14,
  b3 = 24,
  chiEff = 144,
  kGimel = 12.31831,

  (* Compactification scale *)
  mGUT = 2.1*^16,  (* GeV *)
  mPlanck = 2.435*^18,  (* GeV *)

  (* Seesaw parameters *)
  yNu = 0.5,  (* Dirac Yukawa coupling *)
  vEW = 246   (* GeV *)
},
  (* Seesaw formula: m_ν ~ (y_ν * v)² / M_R *)
  (* Right-handed mass from G₂ moduli: M_R ~ M_GUT / kGimel *)

  mR = mGUT / kGimel;

  (* Atmospheric splitting from 3-cycle volume *)
  (* Δm²_atm ~ (y_ν * v)² * (b₃/χ_eff) / M_R *)

  deltaM31Sq = (yNu * vEW)^2 / mR * (b3 / chiEff) * 1.0e18;  (* Convert to eV² *)

  (* Solar splitting from 2-cycle topology *)
  (* Δm²_sol ~ Δm²_atm * (b₂/b₃)² *)

  deltaM21Sq = deltaM31Sq * (b2 / b3)^2;

  (* NuFIT 6.0 Normal Ordering *)
  nufit60_dm21 = 7.41e-5;  (* eV² *)
  nufit60_dm31 = 2.507e-3;  (* eV² *)

  deviation31 = (deltaM31Sq - nufit60_dm31) / nufit60_dm31 * 100;
  deviation21 = (deltaM21Sq - nufit60_dm21) / nufit60_dm21 * 100;

  {
    {"Δm²₃₁", N[deltaM31Sq, 4], nufit60_dm31, N[deviation31, 3]},
    {"Δm²₂₁", N[deltaM21Sq, 4], nufit60_dm21, N[deviation21, 3]}
  }
]
