With[{
  (* G2 topological invariants *)
  b2 = 14,
  b3 = 24,
  chiEff = 144,
  kGimel = 12.31831,

  (* Compactification scale *)
  mGUT = 2.1*^16,  (* GeV *)
  mPlanck = 2.435*^18,  (* GeV *)

  (* Seesaw parameters *)
  yNu = 0.5,  (* Dirac Yukawa coupling *)
  vEW = 246   (* GeV *)
},
  (* Seesaw formula: m_nu ~ (y_nu * v)^2 / M_R *)
  (* Right-handed mass from G2 moduli: M_R ~ M_GUT / kGimel *)

  mR = mGUT / kGimel;

  (* Atmospheric splitting from 3-cycle volume *)
  deltaM31Sq = (yNu * vEW)^2 / mR * (b3 / chiEff) * 1.0e18;

  (* Solar splitting from 2-cycle topology *)
  deltaM21Sq = deltaM31Sq * (b2 / b3)^2;

  (* NuFIT 6.0 Normal Ordering *)
  nufit60_dm21 = 7.41e-5;
  nufit60_dm31 = 2.507e-3;

  {N[deltaM31Sq, 4], N[deltaM21Sq, 4]}
]
