With[{
  hbar = 1.055e-34,  (* J*s *)
  EG = 1e-32  (* J - gravitational self-energy scale *)
},
  (* Penrose objective reduction timescale *)
  (* tau = hbar / E_G *)

  tau = hbar / EG;
  tauMs = tau * 1000;  (* Convert to milliseconds *)

  (* Neural processing timescale *)
  neuralMs = 10;  (* ~10 ms for conscious events *)

  {N[tauMs, 3], neuralMs}
]
