With[{
  b3 = 24,
  kGimel = 12.318310,
  cKaf = 27.2,
  gamma = 0.57721566  (* Euler-Mascheroni constant *)
},
  (* Mass ratio from geometric constants *)
  (* m_p/m_e = c_kaf^2 * (k_gimel/Pi) / (correction factor) *)

  numerator = cKaf^2 * (kGimel / Pi);
  denominator = 1.5427972 * (1 + gamma/24);

  massRatio = numerator / denominator;

  (* CODATA 2022 value *)
  codata2022 = 1836.15267343;
  codataErr = 0.00000011;

  deviation = (massRatio - codata2022) / codataErr;

  {N[massRatio, 11], N[deviation, 3]}
]
