With[{
  (* GUT scale from G2 geometry *)
  MGUT = 2.1 * 10^16,  (* GeV *)

  (* GUT coupling from b3 *)
  b3 = 24,
  alphaGUT = 1/b3,  (* = 1/24 *)

  (* Proton mass *)
  mp = 0.938,  (* GeV *)

  (* Suppression factor from TCS discrete torsion *)
  S = 2.125
},
  (* Proton decay rate from dimension-6 operators *)
  (* Gamma ~ alpha_GUT^2 * m_p^5 / M_GUT^4 *)

  (* Lifetime in inverse GeV *)
  tauGeV = (MGUT^4 / (alphaGUT^2 * mp^5)) * S^2;

  (* Convert to seconds: 1 GeV^-1 = 6.58e-25 s *)
  tauSec = tauGeV * 6.58 * 10^(-25);

  (* Convert to years *)
  tauYears = tauSec / (3.154 * 10^7);

  (* Super-K bound *)
  superKBound = 1.6 * 10^34;  (* years, for p -> e+ pi0 *)

  (* Check satisfaction *)
  boundSatisfied = (tauYears > superKBound);

  {N[Log10[tauYears], 2], boundSatisfied, "LOCKED"}
]
