wolfram_code:
With[{
  cKaf = 27.2,
  z = 0.45,  (* Redshift for S8 measurement *)
  S8_Planck = 0.832  (* Planck CMB value *)
},
  (* Bulk viscosity suppression from G2 moduli *)
  (* suppression = 1/(1 + (c_kaf/(2*Pi^2) * Sqrt[z])/100) *)

  viscosityTerm = cKaf / (2 * Pi^2) * Sqrt[z];
  suppression = 1 / (1 + viscosityTerm / 100);

  S8_PM = S8_Planck * suppression;

  (* KiDS-1000 measurement *)
  S8_KiDS = 0.759;
  S8_KiDS_err = 0.024;

  deviation = (S8_PM - S8_KiDS) / S8_KiDS_err;

  {N[S8_PM, 4], N[deviation, 3]}
]
