With[{
  (* Tribimaximal mixing base *)
  (* sin^2(theta_12) = 1/3 exactly *)
  sinSqTheta12Base = 1/3,

  (* G2 correction from Hopf fibration *)
  b3 = 24,
  hopfCorrection = -0.02,  (* From S3 -> S2 structure *)

  (* Corrected value *)
  sinSqTheta12 = sinSqTheta12Base + hopfCorrection
},
  (* Convert to angle in degrees *)
  theta12 = ArcSin[Sqrt[sinSqTheta12]] * (180/Pi);

  (* NuFit 6.0 experimental value *)
  theta12Exp = 33.41;  (* degrees *)
  sigma = 0.77;  (* degrees *)

  (* Calculate deviation *)
  deviation = Abs[theta12 - theta12Exp] / sigma;

  {N[theta12, 4], N[sinSqTheta12, 4], N[deviation, 2], "LOCKED"}
]
