wolfram_code:
With[{
  b3 = 24,
  spinorDOF = 8
},
  (* Number of generations from index theorem *)
  (* n_gen = b₃ / (spinor DOF in 7D) *)
  (* 7D spinor has 8 real components *)
  (* Index theorem: n_gen = (1/2)*integral(A-hat) = b₃/8 *)

  nGen = b3 / spinorDOF;

  (* Verify this is exactly 3 *)
  {nGen, nGen == 3}
]
