With[{
  b2 = 14,
  b3 = 24,
  nGen = 3
},
  (* CP-violating phase from topological matching *)
  (* Formula: δ_CP = π * ((n_gen + b₂)/(2*n_gen) + n_gen/b₃) *)

  term1 = (nGen + b2) / (2 * nGen);  (* Generation-Betti mixing *)
  term2 = nGen / b3;  (* Generation-flux ratio *)

  deltaCPRad = Pi * (term1 + term2);
  deltaCPDeg = deltaCPRad * 180/Pi;

  (* Wrap to [0, 360] range *)
  deltaCPDegWrapped = Mod[deltaCPDeg, 360];

  (* NuFIT 6.0 Normal Ordering *)
  nufit60NO = 232.5;  (* degrees *)
  nufit60Uncertainty = 26.7;  (* degrees *)

  deviation = (deltaCPDegWrapped - nufit60NO) / nufit60Uncertainty;

  {N[deltaCPDegWrapped, 4], N[deltaCPRad, 5], N[deviation, 3]}
]
