wolfram_code:
With[{
  (* G₂ moduli parameters *)
  kGimel = 12.31831,
  cKaf = 27.2,
  b3 = 24,
  chiEff = 144,

  (* Present-day w₀ *)
  w0 = -11/13
},
  (* CPL parameterization: w(z) = w₀ + w_a * z/(1+z) *)
  (* Evolution parameter from modulus rolling *)

  (* Method 1: Gaussian pulse width correction *)
  (* w_a ~ -2*(1 + w₀) * (σ_pneuma / z_crit) *)
  sigmaPneuma = cKaf * 2;  (* ~54.4 *)
  zCrit = 3540;

  wa1 = -2 * (1 + w0) * (sigmaPneuma / zCrit);

  (* Method 2: Topological correction *)
  (* w_a ~ -(1 + w₀) * (b₃/χ_eff) *)

  wa2 = -(1 + w0) * (b3 / chiEff);

  (* Method 3: Holonomy correction *)
  (* w_a ~ -(1 + w₀) * (2/k_gimel) *)

  wa3 = -(1 + w0) * (2 / kGimel);

  (* DESI 2025 result *)
  desi2025 = -0.99;
  desiUncertainty = 0.38;

  (* Average of methods *)
  waAverage = Mean[{wa1, wa2, wa3}];

  deviation = (waAverage - desi2025) / desiUncertainty;

  {N[wa1, 4], N[wa2, 4], N[wa3, 4], N[waAverage, 4], N[deviation, 3]}
]
