wolfram_code:
With[{
  b2 = 14,
  b3 = 24,
  nGen = 3,
  chiEff = 144,
  S_orient = 12
},
  (* Atmospheric angle from maximal mixing + topological perturbation *)
  (* Base: tribimaximal θ₂₃ = 45° (maximal) *)
  (* Perturbation from b₂, n_gen asymmetry and orientation *)

  thetaTBM = 45.0;  (* degrees *)

  (* Deviation from maximality *)
  (* δθ₂₃ = (b₂ - n_gen)*n_gen/b₂ + (S_orient/b₃)*(b₂*χ_eff)/(b₃*n_gen) *)

  term1 = (b2 - nGen) * nGen / b2;  (* Betti asymmetry *)
  term2 = (S_orient / b3) * (b2 * chiEff) / (b3 * nGen);  (* Orientation *)

  deltaTheta23 = term1 + term2;
  theta23Deg = thetaTBM + deltaTheta23;

  (* NuFIT 6.0 Normal Ordering *)
  nufit60NO = 49.0;  (* degrees *)
  nufit60Uncertainty = 1.3;  (* degrees *)

  deviation = (theta23Deg - nufit60NO) / nufit60Uncertainty;

  {N[theta23Deg, 4], N[deltaTheta23, 4], N[deviation, 3]}
]
