wolfram_code:
With[{
  (* v16.2 geometric predictions *)
  w0 = -23/24,  (* = -0.9583 from G2 *)
  wa = -1/Sqrt[24],  (* = -0.2041 base value *)
  z = 0.45  (* Pivot redshift *)
},
  (* CPL parameterization: w(z) = w0 + wa * z/(1+z) *)

  wz = w0 + wa * (z / (1 + z));

  (* DESI DR2 at z=0.45 *)
  desiWz = -0.95;
  desiErr = 0.10;

  deviation = (wz - desiWz) / desiErr;

  {N[wz, 5], N[deviation, 3]}
]
