wolfram_code:
With[{
  b2 = 14,
  b3 = 24,
  nGen = 3,
  chiEff = 144,
  S_orient = 12  (* Orientation correction from K=4 fibres *)
},
  (* Reactor angle from 3-cycle wavefunction overlap *)
  (* Formula: sin(θ₁₃) = sqrt(b₂*n_gen)/b₃ * (1 + S_orient/(2*χ_eff)) *)

  baseFactor = Sqrt[b2 * nGen] / b3;
  orientationCorrection = 1 + S_orient / (2 * chiEff);

  sinTheta13 = baseFactor * orientationCorrection;
  theta13Deg = ArcSin[sinTheta13] * 180/Pi;

  (* NuFIT 6.0 Normal Ordering *)
  nufit60NO = 8.58;  (* degrees *)
  nufit60Uncertainty = 0.11;  (* degrees *)

  deviation = (theta13Deg - nufit60NO) / nufit60Uncertainty;

  {N[theta13Deg, 4], N[sinTheta13, 5], N[deviation, 3]}
]
